    @Override
    public void paintComponent(Graphics g) {
        super.paintComponent(g);
        ic = new ImageIcon(getClass().getResource(getImageDirectory()));
        g.drawImage(ic.getImage(), 0, 0, this.getWidth(), this.getHeight(), this);
    }

